#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\user\go\pb2ts\packages\pb2ts\node_modules\tree-kill\node_modules;D:\user\go\pb2ts\packages\pb2ts\node_modules;D:\user\go\pb2ts\packages\node_modules;D:\user\go\pb2ts\node_modules;D:\user\go\node_modules;D:\user\node_modules;D:\node_modules;D:\user\go\pb2ts\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/user/go/pb2ts/packages/pb2ts/node_modules/tree-kill/node_modules:/mnt/d/user/go/pb2ts/packages/pb2ts/node_modules:/mnt/d/user/go/pb2ts/packages/node_modules:/mnt/d/user/go/pb2ts/node_modules:/mnt/d/user/go/node_modules:/mnt/d/user/node_modules:/mnt/d/node_modules:/mnt/d/user/go/pb2ts/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../tree-kill/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../tree-kill/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../tree-kill/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../tree-kill/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
